
public class Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int x=1;
     System.out.println("While loop");
     while(x<=100){
    	 System.out.println(x);
    	 x++;
     }
     System.out.println("For loop");
     for (int i=1;i<=100;i++)
     {
    	 System.out.println(i);
     }	
     }

}
